import '@mdi/font/css/materialdesignicons.css';
import ShopCard from '@/components/ShopCard.vue';
export default {
  name: 'NavBar',
  components: {
    ShopCard
  },
  data() {
    return {
      isLoading: false,
      searchingText: "",
      zeroFounds: false
    };
  },
  methods: {
    async requestForCards() {
      // TODO request dla wyszukwarki

      const True = true;
      if (!True) {
        this.zeroFounds = false;
      } else {
        this.zeroFounds = true;
      }
      this.isLoading = false;
    }
  },
  props: {
    numberOfCards: {
      type: String,
      require: true
    },
    seller: {
      type: Boolean,
      require: true
    }
  },
  mounted() {
    let timer = null;
    const waitTime = 1000;
    const input = document.querySelector('input');
    if (this.searchingText === "") {
      input.addEventListener('input', () => {
        clearTimeout(timer);
        this.isLoading = true;
        this.zeroFounds = false;
        timer = setTimeout(() => {
          if (this.searchingText === input.value) {
            this.requestForCards();
          }
        }, waitTime);
      });
    }
  }
};
