import axios from 'axios';
export default {
  name: 'ShowLogin',
  data() {
    return {
      reg: false,
      username: "",
      email: "",
      password: "",
      nameRules: [v => !!v || "Username is required", v => /^[a-zA-Z]{3,15}$/i.test(v) || "Nazwa użytkownika nie jest poprawna"],
      emailRules: [v => !!v || "Email is required", v => /^[^\s@]+@[^\s@]+\.[^\s@]+$/i.test(v) || "Email nie jest poprawny"],
      passwordRules: [v => !!v || "Password is required", v => /^[a-zA-Z]{8,100}$/i.test(v) || "Hasło nie jest poprawne"]
    };
  },
  methods: {
    login() {
      axios.post('/login_user', {
        name: this.username,
        email: this.email,
        password: this.password
      }, {
        headers: {
          'Content-Type': 'application/json'
        }
      }).then(response => {
        const obj = JSON.parse(response.config.data);
        localStorage.setItem("user_name", obj.name);
        localStorage.setItem("user_email", obj.email);
        localStorage.setItem("user_password", obj.password);
      });
    },
    register() {
      axios.post('/add_user', {
        name: this.username,
        email: this.email,
        password: this.password
      }, {
        headers: {
          'Content-Type': 'application/json'
        }
      }).then(response => {
        const obj = JSON.parse(response.config.data);
        localStorage.setItem("user_name", obj.name);
        localStorage.setItem("user_email", obj.email);
        localStorage.setItem("user_password", obj.password);
        window.location.href = '/chat';
      });
    }
  },
  computed: {
    valid() {
      return !!this.password && !!this.email && !!this.username;
    }
  }
};
